from collections import OrderedDictfrom collections import defaultdictN = int(input())dct = OrderedDict()d = defaultdict(list)for i in range(N):    name_price = input().split()    string = ' '.join(name_price[:-1])    d[string].append(int(name_price[-1]))for i, j in d.items():    print(i, sum(j))#     if string not in dct:#         dct[string] = int(name_price[-1])#     else:#         dct[string] += int(name_price[-1])# for i, j in dct.items():#     print(i, j)